package com.primitivedatatypes;

public class Integerdatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           
		byte b=127;
		b++;
		
		short s=100;
		++s;
		s--;
		
		int i=150;
		--i;
		i++;
		
		long l=200l;
		l--;
		
		System.out.println(b-s-i+l);
	}

}
